create or replace view db2inst1.orf_gene_dump2_v as WITH orf AS ( SELECT f.FEATURE_ID AS orf_id, 'http://xenbase.org/reagents/displayOrf.do?featureId='||f.feature_id orf_url, f.NAME AS orf_name FROM feature f WHERE 1=1 --AND LOWER(VARCHAR(name)) LIKE 'aagab orf1' --AND f.feature_id = 19775626 --17254137 AND f.type_id = 40593 AND is_analysis = 1 -- exclude the transgene ORF's
), tmpl_acc as ( SELECT t10.orf_id, LISTAGG(distinct coalesce(atg.accession,''),'|') template_accession , LISTAGG(distinct coalesce(atg.gi ,''),'|') template_gi, count(*) reccount_atg --atg.gi FROM feature f5, feature_relationship fr5, accession_to_gi atg, orf t10 WHERE fr5.subject_id = t10.orf_id AND f5.feature_id = fr5.object_id AND fr5.type_id = 607 AND f5.type_id = 738 AND f5.name = atg.accession group by t10.orf_id
), orfGene AS ( SELECT o.orf_id, o.orf_name, o.orf_url, g1.gene_id as orfGene_id, g1.symbol as orfGene_name, g1.organism_id, (SELECT max(dbx.accession) FROM feature_dbxref fd, dbxref dbx WHERE dbx.db_id = 46 AND dbx.dbxref_id = fd.dbxref_id AND fd.feature_id = g1.gene_id ) ENTREZ_GENE_ID -- get gene page , ( SELECT max('XB-GENEPAGE-' || gm2.gene_id ) -- max( gm2.gene_id ) FROM feature_relationship fr1, feature_relationship fr2, feature_relationship fr3, feature f, genes_mqt gm1, genes_mqt gm2 WHERE fr1.subject_id = o.orf_id -- ORF from cDNA --> AND fr1.type_id = 607 -- derives_from --> AND f.feature_id = fr1.object_id AND f.type_id = 738 -- mRNA --> AND fr2.subject_id = f.feature_id AND fr2.type_id = 607 -- derives_from --> AND gm1.gene_id = fr2.object_id AND gm1.gene_id = fr3.subject_id AND fr3.type_id = 500 -- is_a --> AND fr3.object_id = gm2.gene_id ) umbrella_gene_id , ( select count(*) --'XB-GENEPAGE-' ||genepage.feature_id from feature_relationship fr2, feature genepage where genepage.feature_id = fr2.object_id AND fr2.type_id = 500 AND fr2.subject_id = g1.gene_id
) as has_umbrella_gene -- FROM orf o, feature_relationship fr1, feature_relationship fr2, feature f1, genes_mqt g1 WHERE fr1.subject_id = o.orf_id AND fr1.type_id = 607 -- derives_from --> AND f1.feature_id = fr1.object_id AND f1.type_id = 738 --mRNA AND fr2.subject_id = f1.feature_id AND fr2.type_id = 607 -- derives_from --> AND g1.gene_id = fr2.object_id AND g1.organism_id IN (11, 12, 13) ) , m2a as ( select t10.* , row_number() over ( partition by t10.orf_id order by t10.has_umbrella_gene desc ) rankit from orfGene t10 ), m2 as ( select t10.orf_id, t10.orf_name, t10.orf_url, t10.organism_id, count(*) reccount , LISTAGG(t10.orfGene_id,'|') WITHIN GROUP (ORDER BY t10.orfGene_id) list_orfGene_id , LISTAGG(coalesce(t10.orfGene_name,''),'|') WITHIN GROUP (ORDER BY t10.orfGene_id) list_orfGene_name --, LISTAGG(coalesce(t10.umbrella_gene_id,''),'|') WITHIN GROUP (ORDER BY t10.umbrella_gene_id) list_Genepage_ID , LISTAGG(distinct coalesce(t10.umbrella_gene_id,''),'|') list_Genepage_ID , LISTAGG(distinct coalesce(t10.ENTREZ_GENE_ID,''),'|') list_ENTREZ_GENE_ID , LISTAGG(distinct coalesce(t10.has_umbrella_gene,''),'|') list_has_umbrella_gene --, rank() over ( partition by t10.orf_id order by has_umbrella_gene desc ) rankit --, max(coalesce(has_umbrella_gene,0)) max_has_umbrella_gene from m2a t10 where t10.rankit = 1 --where has_umbrella_gene is null --where t10.organism_id = 12 group by t10.orf_id, t10.orf_name, t10.orf_url, t10.organism_id ), m3 as ( select t10.* ,(SELECT max(fp.value) FROM featureprop fp WHERE fp.feature_id = t10.orf_id AND fp.type_id = 31610 ) FULL_LENGTH_CONFIDENCE ,(SELECT max(fp.value) FROM featureprop fp WHERE fp.feature_id = t10.orf_id AND fp.type_id = 40632 ) PLATE ,(SELECT max(fp.value) FROM featureprop fp WHERE fp.feature_id = t10.orf_id AND fp.type_id = 40633 ) WELL , (select max(t20.template_accession) from tmpl_acc t20 where t20.orf_id = t10.orf_id) as template_accession , (select max(t20.template_gi) from tmpl_acc t20 where t20.orf_id = t10.orf_id) as template_gi --, case when t10.organism_id = 12 then 'Verified' else 'Predicted' end ORF_SEQUENCE_STATUS , ( SELECT max(f.name) FROM feature f, feature_relationship fr WHERE f.type_id = 31695 -- five_prime_EST --> AND f.feature_id = fr.subject_id AND fr.type_id = 76 -- expressed_tag_of_gene --> AND fr.object_id = t10.orf_id -- ORF from cDNA --> ) as five_prime_EST_accession , ( SELECT max(f.name) FROM feature f, feature_relationship fr WHERE f.type_id = 31696 -- three_prime_EST --> AND f.feature_id = fr.subject_id AND fr.type_id = 76 -- expressed_tag_of_gene --> AND fr.object_id = t10.orf_id -- ORF from cDNA -->
)as three_prime_EST_accession --,mqt1.full_name , ( SELECT --f.feature_id AS orfId, --f.name AS orfName, f.residues FROM feature f WHERE f.feature_id = t10.orf_id fetch first 1 rows only )as orf_Sequence, (SELECT f.residues FROM feature f, feature_relationship fr WHERE f.type_id = 5072 -- translation --> AND f.feature_id = fr.subject_id AND fr.type_id = 607 -- derives_from --> AND fr.object_id = t10.orf_id -- ORF from cDNA --> fetch first 1 rows only ) as orf_translation from m2 t10 --where t10.list_has_umbrella_gene = '0' --'0|1' ), m4 as ( select 'XB-ORF-' || t10.ORF_ID Xenbase_ORF_ID , t10.ORF_ID ,t10.ORF_NAME ORF_Name ,t10.ORF_URL ,t10.ORGANISM_ID ,t10.RECCOUNT ,t10.list_ENTREZ_GENE_ID ENTREZ_GENE_ID ,t10.LIST_ORFGENE_ID Xenbase_Gene_ID ,t10.LIST_ORFGENE_NAME Xenbase_Gene_Symbol ,t10.LIST_GENEPAGE_ID Xenbase_Genepage_ID ,t10.FULL_LENGTH_CONFIDENCE ,t10.PLATE ,t10.WELL ,t10.template_accession ,t10.template_gi ,t10.FIVE_PRIME_EST_ACCESSION ,t10.THREE_PRIME_EST_ACCESSION --,t10.ORF_SEQUENCE_STATUS ,t10.ORF_SEQUENCE ,t10.ORF_TRANSLATION ,t10.list_has_umbrella_gene from m3 t10 --where t10.organism_id = 11 --and list_has_umbrella_gene = '0' --, '1' , '0|1' --and t10.reccount > 1 --order by t10.orf_id, t10.orf_name ) select * from m4

